INSERT INTO autores (codigo_autor, nome_completo, data_nascimento, biografia, nacionalidade)
VALUES
  (1, 'Machado de Assis', '1839-06-21', 'Machado de Assis foi um escritor, poeta, contista, cronista, dramaturgo, jornalista e crítico literário brasileiro. É considerado o maior nome da literatura brasileira e fundador da Academia Brasileira de Letras.', 'Brasileira'),
  (2, 'Stephen King', '1947-09-21', 'Stephen King é um escritor norte-americano, conhecido por seus trabalhos no gênero de terror. Suas obras mais famosas incluem "It", "O Iluminado" e "Carrie, a Estranha".', 'Americana'),
  (3, 'Clarice Lispector', '1920-12-10', 'Clarice Lispector foi uma escritora e jornalista brasileira, nascida na Ucrânia. Sua obra é conhecida por sua intensidade psicológica e estilo inovador. Entre suas obras mais famosas está "A Hora da Estrela".', 'Brasileira'),
  (4, 'J.K. Rowling', '1965-07-31', 'J.K. Rowling é uma escritora britânica, conhecida por ser a autora da série de livros "Harry Potter". Seus livros se tornaram um fenômeno mundial e foram adaptados para o cinema.', 'Britânica'),
  (5, 'Fiódor Dostoiévski', '1821-11-11', 'Fiódor Dostoiévski foi um dos maiores escritores russos do século XIX. Suas obras, como "Crime e Castigo" e "Os Irmãos Karamazov", exploram a psicologia humana e questões existenciais.', 'Russa'),
  (6, 'Jane Austen', '1775-12-16', 'Jane Austen foi uma romancista inglesa conhecida por suas obras satíricas sobre a sociedade e costumes do início do século XIX na Inglaterra. "Orgulho e Preconceito" é uma de suas obras mais famosas.', 'Britânica'),
  (7, 'Mia Couto', '1955-07-05', 'Mia Couto é um escritor moçambicano. Suas obras são reconhecidas por sua linguagem poética e engajamento social. Ele é autor de livros como "Terra Sonâmbula" e "A Confissão da Leoa".', 'Moçambicana'),
  (8, 'Isabel Allende', '1942-08-02', 'Isabel Allende é uma escritora chilena-americana, autora de obras como "A Casa dos Espíritos" e "Eva Luna". Seus livros frequentemente exploram temas de amor, família e política.', 'Chilena'),
  (9, 'Jorge Amado', '1912-08-10', 'Jorge Amado foi um dos mais famosos escritores brasileiros do século XX. Suas obras, como "Dona Flor e Seus Dois Maridos" e "Gabriela, Cravo e Canela", são ícones da literatura brasileira.', 'Brasileira'),
  (10, 'Haruki Murakami', '1949-01-12', 'Haruki Murakami é um escritor japonês, conhecido por seus romances surrealistas e narrativas intricadas. "Norwegian Wood" e "Kafka à Beira-Mar" são alguns de seus trabalhos notáveis.', 'Japonesa'),
  (11, 'Ziraldo', '1932-10-24', 'Ziraldo Alves Pinto é um escritor, cartunista e jornalista brasileiro. Ele é conhecido por suas obras infantis, como "O Menino Maluquinho" e "Flicts".', 'Brasileira'),
  (12, 'Agatha Christie', '1890-09-15', 'Agatha Christie foi uma escritora britânica, famosa por suas obras de mistério e detetive. Seus livros, incluindo "O Assassinato no Expresso Oriente" e "Assassinato no Beco", são clássicos do gênero.', 'Britânica'),
  (13, 'Raphael Montes', '1990-12-10', 'Raphael Montes é um escritor brasileiro, conhecido por seus romances de suspense. Algumas de suas obras incluem "Suicidas" e "Dias Perfeitos".', 'Brasileira'),
  (14, 'Julia Quinn', '1970-03-12', 'Julia Quinn é uma autora americana de romances históricos, conhecida pela série "Os Bridgertons". Seus livros são populares por suas tramas românticas e humorísticas.', 'Americana'),
  (15, 'Taylor Jenkins Reid', '1983-07-07', 'Taylor Jenkins Reid é uma autora norte-americana, conhecida por obras como "Os Sete Maridos de Evelyn Hugo" e "Daisy Jones & The Six". Suas narrativas envolventes conquistaram leitores em todo o mundo.', 'Americana');

