INSERT INTO livros (codigo_livro, titulo, ano_publicacao, isbn, idioma, quantidade_paginas, sinopse, edicao, formato, classificacao_etaria, data_aquisicao, codigo_editora, codigo_autor, codigo_genero)
VALUES
  (1, 'Dom Casmurro', '1899', '978-85-254-0829-1', 'Português', 256, 'Dom Casmurro é um romance de Machado de Assis. A história é narrada por Bento Santiago, o Bentinho, que relembra sua juventude e os eventos que cercam seu relacionamento com Capitu.', '1ª Edição', 'Físico Brochura', 'Livre', '2023-12-16', 1, 1, 2),
  (2, 'It', '1986', '978-85-359-0273-4', 'Inglês', 1104, 'It é um thriller de terror de Stephen King. A história segue um grupo de crianças enfrentando um ser maligno que assume a forma de seus medos mais profundos.', '1ª Edição', 'Audiolivro', '18 anos', '2022-11-17', 2, 2, 3),
  (3, 'A Hora da Estrela', '1977', '978-85-359-0273-4', 'Português', 96, 'A Hora da Estrela é um romance de Clarice Lispector. A obra narra a história de Macabéa, uma mulher simples do Nordeste que busca uma vida melhor no Rio de Janeiro.', '1ª Edição', 'Ebook', '14 anos', '2021-10-18', 3, 3, 5),
  (4, 'Harry Potter e a Pedra Filosofal', '1997', '978-85-325-1176-2', 'Francês', 224, 'Harry Potter e a Pedra Filosofal é o primeiro livro da série de J.K. Rowling. A narrativa segue o jovem bruxo Harry Potter em sua jornada na escola de magia de Hogwarts.', '1ª Edição', 'Físico Capa Dura', 'Livre', '2020-09-19', 4, 4, 4),
  (5, 'Crime e Castigo', '1866', '978-85-359-0273-4', 'Russo', 544, 'Crime e Castigo é um romance de Fiódor Dostoiévski que explora as complexidades psicológicas de um estudante que comete um assassinato. A obra é um clássico da literatura russa.', '1ª Edição', 'Físico Capa Dura', '16 anos', '2023-08-20', 5, 5, 1),
  (6, 'Orgulho e Preconceito', '1813', '978-85-359-0273-4', 'Inglês', 416, 'Orgulho e Preconceito é um romance de Jane Austen. A obra aborda questões sociais e amorosas na sociedade inglesa do século XIX, seguindo a protagonista Elizabeth Bennet.', '1ª Edição', 'Ebook', '12 anos', '2022-07-21', 6, 6, 2),
  (7, 'Terra Sonâmbula', '1992', '978-85-359-0273-4', 'Português', 192, 'Terra Sonâmbula é um romance de Mia Couto que combina elementos de realismo mágico e contos folclóricos moçambicanos. A história segue dois personagens em um país devastado pela guerra civil.', '1ª Edição', 'Físico Brochura', '14 anos', '2021-06-22', 7, 7, 1),
  (8, 'A Casa dos Espíritos', '1982', '978-85-359-0273-4', 'Espanhol', 448, 'A Casa dos Espíritos é um romance de Isabel Allende. A narrativa segue a família Trueba ao longo de várias gerações, abordando temas como amor, poder e espiritualidade.', '1ª Edição', 'Audiolivro', '16 anos', '2020-05-01', 8, 8, 5),
  (9, 'Gabriela, Cravo e Canela', '1958', '978-85-359-0273-4', 'Português', 432, 'Gabriela, Cravo e Canela é um romance de Jorge Amado. Situado na Bahia, o livro aborda questões sociais, políticas e culturais, além de explorar o romance entre Gabriela e Nacib.', '1ª Edição', 'Ebook', 'Livre', '2023-04-02', 9, 9, 15),
  (10, 'Norwegian Wood', '1987', '978-85-359-0273-4', 'Japonês', 368, 'Norwegian Wood é um romance de Haruki Murakami. A história segue Toru Watanabe enquanto ele reflete sobre seu relacionamento com Naoko e Midori durante os anos 1960 no Japão.', '1ª Edição', 'Físico Capa Dura', '16 anos', '2022-03-03', 10, 10, 2),
  (11, 'O Menino Maluquinho', '1980', '978-85-359-0273-4', 'Português', 96, 'O Menino Maluquinho é um livro infantil de Ziraldo que conta as aventuras do menino travesso Maluquinho. Uma história divertida e cheia de imaginação.', '1ª Edição', 'Infantil', 'Livre', '2021-02-04', 11, 11, 12),
  (12, 'Assassinato no Expresso Oriente', '1934', '978-85-359-0273-4', 'Inglês', 264, 'Assassinato no Expresso Oriente é um clássico de Agatha Christie. O detetive Hercule Poirot investiga um assassinato a bordo do luxuoso trem Orient Express, desvendando segredos e intrigas.', '1ª Edição', 'Audiolivro', '14 anos', '2020-01-05', 12, 12, 8),
  (13, 'Jantar Secreto', '2016', '978-85-359-0273-4', 'Português', 360, 'Jantar Secreto é um thriller psicológico de Raphael Montes. A trama envolve quatro estudantes de medicina que decidem realizar jantares secretos com pratos exóticos. Conforme a narrativa avança, segredos sombrios vêm à tona.', '1ª Edição', 'Físico Brochura', '18 anos', '2023-02-06', 13, 13, 14),
  (14, 'O Duque e Eu', '2000', '978-85-359-0273-4', 'Francês', 288, 'O Duque e Eu é um romance histórico de Julia Quinn, parte da série Os Bridgertons. A obra narra o inusitado acordo entre Daphne Bridgerton e Simon Basset, o Duque de Hastings.', '1ª Edição', 'Físico Capa Dura', '18 anos', '2022-03-07', 14, 14, 2),
  (15, 'Os Sete Maridos de Evelyn Hugo', '2017', '978-85-359-0273-4', 'Português', 400, 'Os Sete Maridos de Evelyn Hugo é um romance de Taylor Jenkins Reid. A história acompanha a vida da famosa atriz Evelyn Hugo, revelando seus casamentos e segredos ao longo das décadas de Hollywood.', '1ª Edição', 'Audiolivro', '16 anos', '2021-04-08', 9, 15, 2);
  
