INSERT INTO resenhas (codigo_resenha, avaliacao, titulo_resenha, comentario, data_resenha, codigo_usuario, codigo_livro)
VALUES
  (1, '4 estrelas', 'Ótima Leitura', 'Dom Casmurro é um clássico da literatura brasileira. A narrativa envolvente de Machado de Assis prende o leitor do início ao fim.', '2023-11-16', 1, 1),
  (2, '3 estrelas', 'Bom, mas longo', 'It é um livro emocionante, mas a extensão pode ser desafiadora. O desenvolvimento dos personagens é impressionante.', '2023-11-17', 2, 2),
  (3, '5 estrelas', 'Obra-prima', 'A Hora da Estrela é uma obra única que mexe com as emoções. Clarice Lispector constrói uma narrativa simples e profunda.', '2023-11-18', 3, 3),
  (4, '2 estrelas', 'Infantil Demais', 'Harry Potter e a Pedra Filosofal é bom para crianças, mas achei a trama um pouco infantil para adultos.', '2023-11-19', 4, 4),
  (5, '5 estrelas', 'Intenso e Impactante', 'Crime e Castigo é uma obra-prima que explora as complexidades da mente humana. Dostoiévski cria uma atmosfera intensa.', '2023-11-20', 5, 5),
  (6, '4 estrelas', 'Romance Clássico', 'Orgulho e Preconceito é um romance encantador que transcende o tempo. Jane Austen aborda questões sociais de forma brilhante.', '2023-11-21', 6, 6),
  (7, '3 estrelas', 'Interessante, mas Pesado', 'Terra Sonâmbula é um livro com uma narrativa envolvente, mas a carga emocional pode ser pesada. Recomendado para quem gosta de desafios literários.', '2023-11-22', 7, 7),
  (8, '4 estrelas', 'Família e Espiritualidade', 'A Casa dos Espíritos é uma jornada fascinante pela história de uma família. Isabel Allende mistura magia e realidade de forma única.', '2023-11-23', 8, 8),
  (9, '5 estrelas', 'Cultural e Romântico', 'Gabriela, Cravo e Canela é uma obra-prima de Jorge Amado que captura a essência da Bahia. Um romance que vai além do amor.', '2023-11-24', 9, 9),
  (10, '1 estrela', 'Decepcionante', 'Norwegian Wood não atendeu às minhas expectativas. A trama é lenta e sem brilho, tornando a leitura cansativa.', '2023-11-25', 10, 10),
  (11, '5 estrelas', 'Infância Divertida', 'O Menino Maluquinho é uma viagem nostálgica à infância. Ziraldo cria um personagem cativante que faz qualquer adulto se sentir criança novamente.', '2023-11-26', 11, 11),
  (12, '4 estrelas', 'Intrigante e Bem Escrito', 'Assassinato no Expresso Oriente é um clássico de Agatha Christie. O mistério é envolvente e a resolução é surpreendente.', '2023-11-27', 12, 12),
  (13, '2 estrelas', 'Desconfortavelmente Fascinante', 'Jantar Secreto é um thriller psicológico que mexe com os limites do desconforto. Raphael Montes cria uma trama única, mas algumas cenas são perturbadoras.', '2023-11-28', 13, 13),
  (14, '5 estrelas', 'Romance Encantador', 'O Duque e Eu é um romance histórico que combina paixão e intrigas. Julia Quinn constrói personagens envolventes e uma trama irresistível.', '2023-11-29', 14, 14),
  (15, '5 estrelas', 'Hollywood Revelado', 'Os Sete Maridos de Evelyn Hugo oferece uma visão fascinante dos bastidores de Hollywood. Taylor Jenkins Reid entrega uma história envolvente e emocional.', '2023-11-30', 15, 15);

